let mfd = Buffer.from(msg.payload.advertisement.manufacturerData);

// msg.payload.mman_type = typeof (mfd);
// msg.payload.mman_str = JSON.stringify(mfd);
// msg.payload.mman_obj = JSON.parse(JSON.stringify(mfd));

msg.payload.mman_bl = mfd[3];
msg.payload.mman_tl1 = mfd[4];
msg.payload.mman_tl2 = mfd[5];

return msg;

// prepare array to hold samples with timestamps
var samples = context.get("samples") || [];

// remember incoming sample and forget oldest sample
// keep 60 samples, assuming one hour of history
// when messages coming in at rate 1 per minute
samples.push({value: msg.payload, timestamp: Date.now()});
if (samples.length > 60) {
    samples.shift();
}

// store samples
context.set("samples", samples);

// get oldest and newest sample
let previousSample = samples[0];
let currentSample = samples[samples.length - 1];

msg.previousSample = previousSample;
msg.currentSample = currentSample;

msg.difference = currentSample.value - previousSample.value;
msg.duration = currentSample.timestamp - previousSample.timestamp;

// duration between oldest and newest sample
// assume one hour if not enough samples collected
var duration = msg.duration; 
if (duration <= 0) {
    duration = 60*60*1000;
}

// difference between oldest and newest sample
// assume 1 percent per one hour if not enough samples collected
// or if tanks being filled (positive difference)
var difference = msg.difference;
if (difference >= 0) {
    difference = duration / (60*60*1000) * -1;
}

// compute rate
msg.rate = 1000.0 * difference / duration;

return msg;
